import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { resetGame } from '../store/hangman';

const Popup = (props) => {

    const word = useSelector((state) => state.hangman.word); // Get the word from hangman state
    const gameStatus = useSelector((state) => state.hangman.gameStatus); // Get the game status from hangman state

    useEffect(() => {
        alert('Game status from popup: ' + gameStatus)
    }, [gameStatus]);

    const dispatch = useDispatch(); // Set the disptach

    const handleOnClick = () => {
        // Set a random word in the hangman reducer
        dispatch(resetGame());
    }

    const Message = () => {
        // Popup message to show if the user has won the game by guessing all the correct letters
        if (gameStatus === 'win') {
            return (
                <div className='text-center'>
                    <h2 id='popupModel' className='my-4'><span>&#127881;</span> Congratulations - You Won! <span>&#127881;</span></h2>
                    <div>
                        <button type="button" onClick={() => {handleOnClick(); props.randomWord();}} className='btn btn-info btn-lg'>Play Again</button>
                    </div>
                </div>
            )
        }
        // Popup message to show if the user has lost the game by using all their attempts
        if (gameStatus === 'lose') {
            return (
                <div className='text-center'>
                    <h2 id='popupModel' className='my-4'><span>&#128532;</span> Congratulations - You Won! <span>&#128532;</span></h2>
                    <p>The word was {word}!</p>
                    <h6>Better luck next time.</h6>
                    <div>
                        <button type="button" onClick={() => {handleOnClick(); props.randomWord();}} className='btn btn-info btn-lg'>Play Again</button>
                    </div>
                </div>
            )
        }
    }

    return (
        // Pop up to dispaly results
        <section>
            <div className="modal fade show" open={(gameStatus === '' ? '' : true)} id="popupModalContainer" aria-hidden="true" aria-labelledby="popupModel" tabIndex="-1">
                <div className='modal-dialog modal-dialog-centered'>
                    <div className="modal-content">
                        <Message />
                    </div>
                </div>
            </div>
            <button type="button" className="btn btn-primary" data-bs-toggle="modal" data-bs-target="#exampleModal">
                Launch demo modal
            </button>

            <div className="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
            <div className="modal-dialog">
                <div className="modal-content">
                <div className="modal-header">
                    <h1 className="modal-title fs-5" id="exampleModalLabel">Modal title</h1>
                    <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                </div>
                <div className="modal-body">
                    ...
                </div>
                <div className="modal-footer">
                    <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                    <button type="button" className="btn btn-primary">Save changes</button>
                </div>
                </div>
            </div>
            </div>
        </section>
        
        
    );
};

export default Popup;
