import React from 'react';
import { useDispatch } from 'react-redux';
import { resetGame } from '../store/hangman';

const Reset = (props) => {

    // Set the disptach
    const dispatch = useDispatch(); 

    const handleClick = () => {

        // Set a random word in the hangman reducer
        dispatch(resetGame()); 
            
        // Reset the random word
        props.randomWord();
    }

    return (
        <div className='resetBtnContainer'>
            <button type='button' onClick={() => {handleClick()}} className="btn btn-outline-light btn-sm">Reset</button>
        </div>
    );
};

export default Reset;
