import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { resetGame } from '../store/hangman';

const Popup = (props) => {

    const word = useSelector((state) => state.hangman.word); // Get the word from hangman state
    const gameStatus = useSelector((state) => state.hangman.gameStatus); // Get the game status from hangman state

    const modal = document.querySelector(".modal");
    const overlay = document.querySelector(".overlay");
    const openModalBtn = document.querySelector(".btn-open");
    const closeModalBtn = document.querySelector(".btn-close");


    const openModal = function () {
        modal.classList.remove("hidden");
        overlay.classList.remove("hidden");
    };

    const closeModal = function () {
        modal.classList.add("hidden");
        overlay.classList.add("hidden");
    };

    openModalBtn.addEventListener("click", openModal);

    closeModalBtn.addEventListener("click", closeModal);

    useEffect(() => {
        alert('Game status from popup: ' + gameStatus)
    }, [gameStatus]);

    const dispatch = useDispatch(); // Set the disptach

    const handleOnClick = () => {
        // Set a random word in the hangman reducer
        dispatch(resetGame());
    }

    const Message = () => {
        // Popup message to show if the user has won the game by guessing all the correct letters
        if (gameStatus === 'win') {
            return (
                <div className='text-center'>
                    <h2 id='popupModel' className='my-4'><span>&#127881;</span> Congratulations - You Won! <span>&#127881;</span></h2>
                    <div>
                        <button type="button" onClick={() => {handleOnClick(); props.randomWord();}} className='btn btn-info btn-lg'>Play Again</button>
                    </div>
                </div>
            )
        }
        // Popup message to show if the user has lost the game by using all their attempts
        if (gameStatus === 'lose') {
            return (
                <div className='text-center'>
                    <h2 id='popupModel' className='my-4'><span>&#128532;</span> Congratulations - You Won! <span>&#128532;</span></h2>
                    <p>The word was {word}!</p>
                    <h6>Better luck next time.</h6>
                    <div>
                        <button type="button" onClick={() => {handleOnClick(); props.randomWord();}} className='btn btn-info btn-lg'>Play Again</button>
                    </div>
                </div>
            )
        }
    }

    return (
        // Pop up to dispaly results
        <section>
            <div class="modal hidden">
                <div class="flex">
                    <img src="user.png" width="50px" height="50px" alt="user" />
                    <button class="btn-close">â¨‰</button>
                </div>
                <div>
                    <Message />
                </div>

                <input type="email" id="email" placeholder="brendaneich@js.com" />
                <button class="btn">Submit</button>
            </div>

            <div class="overlay hidden"></div>
            <button class="btn btn-open">Open Modal</button>
        </section>
        
        
    );
};

export default Popup;
