import { createSlice } from "@reduxjs/toolkit";

export const hangmanSlice = createSlice({

    name: 'hangman',

    initialState: {
        wrongLetters: 0,
    },

    reducers: {
        // Add a todo
        updateWrongLetters: (state) => {
            state.wrongLetters = state.wrongLetters + 1;
            }
        },

});

export const { updateWrongLetters } = hangmanSlice.actions;

export default hangmanSlice.reducer;
